CREATE OR REPLACE FUNCTION public.get_salesforce_leads(limit_count integer DEFAULT 100) RETURNS SETOF json LANGUAGE sql SECURITY DEFINER AS $$ SELECT row_to_json(l) FROM salesforce.lead l LIMIT $1; $$; COMMENT ON FUNCTION public.get_salesforce_leads IS 'Get leads from Salesforce schema'; GRANT EXECUTE ON FUNCTION public.get_salesforce_leads TO authenticated; GRANT EXECUTE ON FUNCTION public.get_salesforce_leads TO service_role;
